mapper.select:
select
count(*)
from section where 1=1

  and id = #{bean.id,jdbcType=INTEGER}


  and parentid = #{bean.parentid,jdbcType=INTEGER}


  and name like '%${bean.name}%'

SELECT t1.id, t1.name, t1.img, t1.type, t1.orderby, t1.createtime createtime, t1.createuserid ,t1.parentid,t1.descs, CASE t1.parentid WHEN '0' THEN 'æ— ' ELSE t2.name END fatherName FROM section t1 LEFT JOIN section t2 ON t1.parentid=t2.id WHERE 1=1
and t1.name like '%${bean.name}%'
and t1.id = #{bean.id,jdbcType=INTEGER}
and t1.parentid = #{bean.parentid,jdbcType=INTEGER}
ORDER BY t1.parentid, t1.orderby LIMIT ${pageNo}, ${pageSize}
